import React,{Component} from "react";
import Axios from "axios";

class Login extends Component {

    state = {
            username:'',
            password:'',
            users:[],
            isLoaded:false
        }
    inputChange1=(event)=>{
        console.log(event.target.value);
        //this.state.username=24243;
        //把表单输入的值赋值给username
        this.setState({
            username:event.target.value
        })
    }
    inputChange2=(event)=>{
        console.log(event.target.value);
        //把表单输入的值赋值给username
        this.setState({
            password:event.target.value
        })
    }
    getInput=()=>{
        const t = this;
        if (this.state.username==111&&this.state.password==111) {
            document.getElementById("container").style.display="none";
            const _this=this;
            Axios.get('https://5b5e71c98e9f160014b88cc9.mockapi.io/api/v1/lists')
                .then(function (response) {
                    _this.setState({
                        users:response.data,
                        isLoaded:true
                    });
                })
                .catch(function (error) {
                    console.log(error);
                    _this.setState({
                        isLoaded:false,
                        error:error
                    })
                })

        } else {
            alert("用户名或密码错误！")
        }
        // alert(this.state.username + this.state.password);
    }
    render() {
        return (
            <div>
                <div id="container">
                    <h2>用户登录</h2>
                    <div id="username">用户名：<input  onChange={this.inputChange1} ></input></div>
                    <div id="password">密&nbsp;&nbsp;&nbsp;&nbsp;码：<input  onChange={this.inputChange2} ></input></div>
                    <div><button  onClick={this.getInput} id="btn">登陆</button></div>
                </div>
                <table className="table table-bordered">

                    <tbody>
                    <TrData users={this.state.users}/>
                    </tbody>
                </table>
            </div>
        )
    }
}

class TrData extends React.Component{
    constructor(props){
        super(props);
    }
    render(){
        return (
            this.props.users.map((user,i)=>{
                return (
                    <tr key={user.id} className="text-center">
                        <td>{user.id}</td>
                        <td>{user.name}</td>
                        <td>{user.age}</td>
                        <td>{user.sex}</td>
                    </tr>
                )

            })
        )
    }
}
export default Login;
