import React,{Component}from "react"
import PubSub from "pubsub-js";
import Axios from "axios";
import qs from 'qs'

export default class GetItem extends Component{

    componentDidMount() {
        PubSub.subscribe('token',(msg,tokenmanager)=> this.setState(tokenmanager))//订阅Getinfo发布的token
    }

postitem=()=>{

const data={
    q:  'owner:"admin" orgid:0123456789ABCDEF  ownerfolder:root',
    countFields: "access,contentstatus,categories",
    countSize: 200,
    f:"json",
    token:this.state.tokenmanager
}
    const params=qs.stringify(data);
    Axios.post('https://esrichina3d.arcgisonline.cn/portal/sharing/community/self', params)
        .then((res)=>{
            const {results}=res.data
        this.setState({results})
        })
}

    render (){
        return(
            <div>
                <button onClick={this.postitem}>获取项目</button>
                <div>
                    {
                        this.state.results.map((resultsobj)=>{
                            return(
                                <div>
                                   <a><img src={resultsobj.thumbnail}/></a>
                                    <p>{resultsobj.title}</p>
                        </div>
                            )
                        })
                    }
                </div>
            </div>
        )

    }
}
