import React,{Component} from "react";
import Axios from "axios";
import PubSub from 'pubsub-js'
import qs from 'qs'

export default class Login extends Component {
state={
    username:"",
    password:""
}

input1=(event)=>
{
    this.setState({username:event.target.value})

}
    input2=(event)=>
    {
        this.setState({password:event.target.value})

    }
postinput=()=> {
    // 这么写可以 start
    const data = {
        // username: this.state.username,
        // password: this.state.password,
        username: 'Product',
        password: 'Esri1234ArcGIS',
        referer: "localhost:3000", // URL of the sending app.
        expiration: 60, // Lifetime of the token in minutes.
        f: "json"
    }
    const params = qs.stringify(data);//将数据转化为字符串形式
    Axios.post('https://esrichina3d.arcgisonline.cn/portal/sharing/rest/generateToken', params)
        .then(
            (res) => {
           PubSub.publish('token',{token:res.data.token});//将token发布
        })
}

    render()
    {
        return (
            <div>
                <input onChange={this.input1} name="username"/><br/>
                <input onChange={this.input2} password="password"/>
                <button onClick={this.postinput}>获取token</button>

            </div>
        )}
}
