import React,{Component} from "react";
import Axios from "axios";


export default class Login extends Component {
state={
    username:"",
    password:""
}

input1=(event)=>
{
    this.setState({username:event.target.value})
}
    input2=(event)=>
    {
        this.setState({password:event.target.value}
        )
    }
postinput=()=>{
    Axios({
   method:'post',
        headers:{'Content-Type': 'application/x-www-form-urlencoded;charset=utf-8'},
        url:'https://moon.latern.com/portal/sharing/rest/generateToken',
   data:{
       username: this.state.username,
       password: this.state.password,
       referer: "localhost", // URL of the sending app.
       expiration: 60, // Lifetime of the token in minutes.
       f: "json"
   },


    })
        .then((res)=>
    {
console.log(res.data)
    })

    }


render(){
    return(
        <div>
<input onChange={this.input1} name="username"/><br/>
<input onChange={this.input2} password="password"/>
<button onClick={this.postinput}>获取token</button>

        </div>
    )
}


}
