import React,{Component}from "react"
import PubSub from "pubsub-js";
import Axios from "axios";
import qs from 'qs'

export default class GetItem extends Component {
    state = {
        results: [],
        arr: [],
        data:[]

    }

    componentDidMount() {
        PubSub.subscribe('token', (msg, tokenmanager) => this.setState(tokenmanager))//订阅Getinfo发布的token
    }

    postitem = () => {

        const data = {
            q: 'owner:"admin" orgid:0123456789ABCDEF  ownerfolder:root -type:("Code Attachment" OR "Featured Items") -typekeywords:("MapAreaPackage") -type:("Map Area")',
            num: 20,
            start: 1,
            sortField: "modified",
            sortOrder: "desc",
            f: "json",
            token: this.state.tokenmanager
        }
        const params = qs.stringify(data);
        Axios.post('https://p15v.arcgisonline.cn/portal/sharing/rest/search', params)
            /*将获取到的数据提出，存储到state里*/
            .then(
                (res) => {
                    const {results} = res.data
                    this.setState({results})
                    const arr = []
                    this.state.results.map(obj => {

                        const {id} = obj
                        return (
                            arr.push({id})
                        )
                    })
                    this.setState({arr})
                }
            )


    }

    render() {
        return (
            <div>
                <button onClick={this.postitem}>获取项目</button>
                <div>
                    {

                        /*利用.map遍历数组内的值*/
                        this.state.results.map((resultsobj) => {

                            Axios.get('https://p15v.arcgisonline.cn/portal/sharing/rest/content/items/' + resultsobj.id + '/info/thumbnail/Thumbnail.png', {params: {token: this.state.tokenmanager}})
                                .then((res) => {
                             const {data}=res
                           this.setState(data)
                                })

                            return (

                                <div key={resultsobj.id}>
                                    <a href="https://p15v.arcgisonline.cn/portal/home"><img alt=""
                                     src={this.state.data}/></a>
                                    <p>{resultsobj.title}</p>
                                </div>
                            )
                        })
                    }
                </div>
            </div>
        )

    }
}
