import React,{Component} from "react";
import Axios from "axios";
import qs from 'qs'

export default class Login extends Component {
state={
    username:'',
    password:''
}
input1=(event)=>
{
    this.setState({username:event.target.value}
    )
}
    input2=(event)=>
    {
        this.setState({password:event.target.value}
        )
    }
postinput=()=>{
    const usernamejson=qs.parse(this.state.username)
    const passwordjson=qs.parse(this.state.password)

    Axios({
   method:'post',
        headers:{'Content-Type': 'application/x-www-form-urlencoded;charset=utf-8'},
        url:'https://moon.latern.com/portal/sharing/rest/generateToken',
   data:{
       username: usernamejson,
       password: passwordjson,
       referer: qs.stringify("localhost"), // URL of the sending app.
       expiration: 60, // Lifetime of the token in minutes.
       f: qs.stringify("json")
   },


    })
        .then((res)=>
    {
console.log(res.data)
    })

    }


render(){
    return(
        <div>
<input onChange={this.input1} name="username"/><br/>
<input onChange={this.input2} password="password"/>
<button onClick={this.postinput}>获取token</button>

        </div>
    )
}


}
