import React,{Component} from "react";
import Axios from "axios";
import qs from 'qs'

export default class Login extends Component {
state={
    username:"",
    password:""
}

input1=(event)=>
{
    this.setState({username:event.target.value})
    qs.stringify(this.state.username)
}
    input2=(event)=>
    {
        this.setState({password:event.target.value})
        qs.stringify(this.state.password)
    }
postinput=()=>{
    // 这么写可以 start
    const data = {
        username: 'admin',
        password: 'geoscene',
        //    username: this.state.username,
        //    password: this.state.password,
        referer: "localhost:3000", // URL of the sending app.
        expiration: 60, // Lifetime of the token in minutes.
        f: "json"
    }
    const params = qs.stringify(data);
    Axios.post('https://moon.latern.com/portal/sharing/rest/generateToken', params)
        .then((res) => {
            response => {console.log('成功了',response.data)},
            error => {console.log('失败了',error);}


        })

    }


render(){
    return(
        <div>
<input onChange={this.input1} name="username"/><br/>
<input onChange={this.input2} password="password"/>
<button onClick={this.postinput}>获取token</button>

        </div>
    )
}


}
